<?=view(VENDORPATH.'includes/top-links')?><div class="MainSectionPage onlyform">  <div class="registerPage">    <div class="Login-Sec">      <h1>Forgot Password</h1>      <p>Enter Your Registered Email ID</p>       <form>         <div class="input-SecLogIn">          <div class="EmailId-Sec mt-4">            <label>Email</label>            <input type="email" class="form-control emailOnly" autocomplete="off" required name="email" id="email" placeholder="Email ID">          </div>                   <div class="mt-4">            <button class="Otp_requestBtn" id="submit" onclick="return forgotPassword()">Submit</button>            <p class="text"><a href="<?=base_url(VENDORPATH.'login')?>">Back To Login</a></p>          </div>        </div>      </form>    </div>  </div></div><script>    function forgotPassword(){        var email= $('#email').val().trim();                if(email == ""){            toastr.error("Enter Email");            return false;        }else{             $.ajax({         type: 'POST',         url: '<?=base_url(VENDORPATH.'forgotPassword')?>',         data: {           'email': email,         },         dataType: 'json',         beforeSend: () => {          $('#submit').text('Please Wait ...').prop('disabled', true);         },         success: (res) => {           if (res.status == true) {             toastr.success(res.message);             $('#submit').text('Submit').prop('disabled', false);             $('#email').val('');           } else {             $('#submit').text('Submit').prop('disabled', false);             toastr.error(res.message);           }         }       });        }    }</script><?=view(VENDORPATH.'includes/footer')?>